public static final int FROM_PROJECT = 1;

public static final int FROM_USER = 2;

public boolean isFromProject()
{
    return from == FROM_PROJECT;
}

public boolean isFromUser()
{
    return from == FROM_USER;
}

public String getElapsedTime()
{
    return getTimeDifference( startTime, getSystemTime() );
}

public String getDurationTime()
{
    return getTimeDifference( startTime, endTime );
}
private long getSystemTime()
{
    return java.util.Calendar.getInstance().getTime().getTime();
}

private String getTimeDifference( long startTime, long endTime )
{
    long start = startTime;
    long end = endTime;

    if ( start == 0 )
    {
        return "";
    }

    if ( end == 0 )
    {
        end = getSystemTime();
    }

    int timeInSeconds = (int) ( ( end - start ) / 1000 );
    int days, hours, minutes, seconds;
    days = timeInSeconds / 86400;
    timeInSeconds = timeInSeconds - ( days * 86400 );
    hours = timeInSeconds / 3600;
    timeInSeconds = timeInSeconds - ( hours * 3600 );
    minutes = timeInSeconds / 60;
    timeInSeconds = timeInSeconds - ( minutes * 60 );
    seconds = timeInSeconds;

    String elapsedTime = "";
    if ( days > 0 )
    {
        elapsedTime = days + " d ";
        elapsedTime += hours + " h ";
        elapsedTime += minutes + " min ";
        elapsedTime += seconds + " sec";
    }
    else
    {
        if ( hours > 0 )
        {
            elapsedTime = hours + " h ";
            elapsedTime += minutes + " min ";
            elapsedTime += seconds + " sec";
        }
        else
        {
            if ( minutes > 0 )
            {
                elapsedTime = minutes + " min ";
                elapsedTime += seconds + " sec";
            }
            else
            {
                elapsedTime = seconds + " sec";
            }
        }
    }

    return elapsedTime;
}

/**
 * @return Returns string representation of the changeset
 */
public String toString()
{
    StringBuilder result = new StringBuilder();
    if ( author != null )
    {
        result.append( author ).append( "\n" );
    }
    result.append( date ).append( "\n" );

    if ( files != null )
    {
        for ( java.util.Iterator i = files.iterator(); i.hasNext(); )
        {
            ChangeFile file = (ChangeFile) i.next();

            if (file != null)
            {
                result.append( file ).append( "\n" );
            }
        }
    }

    if ( comment != null )
    {
        result.append( comment );
    }

    return result.toString();
}

/**
 * @see java.lang.Object#equals(java.lang.Object)
 */
public boolean equals( Object obj )
{
    if ( obj instanceof ChangeSet )
    {
        ChangeSet changeSet = (ChangeSet) obj;

        if ( toString().equals( changeSet.toString() ) )
        {
            return true;
        }
    }

    return false;
}

public java.util.Date getDateAsDate()
{
    if ( date > 0 )
    {
        return new java.util.Date( date );
    }

    return null;
}

/**
 * Provide a version of the object as a string for debugging purposes
 *
 * @return a {@link String}made up of the properties of the object
 */
public String toString()
{
    StringBuffer buffer = new StringBuffer( getName() );

    if ( getRevision() != null )
    {
        buffer.append( ", " ).append( getRevision() );
    }

    return buffer.toString();
}

